import sysFileNameRead = sys.argv[1]FileNameWrite = sys.argv[2]Count = 0Write = []CurrentLine = ''number_of_lines = Nonedef change_list(Count, Write, number_of_lines):    number = open(FileNameRead, 'r')    while number_of_lines != '':        number_of_lines = number.readline()        Count += 1    number.close()    number1 = open(FileNameRead, 'r')    for i in range(Count):       append_list = number1.readline()       Write.append(append_list)    number1.close()    return Count, Writedef change_file():    append = open(FileNameWrite, 'w')    for i in range(1, len(Write)):        CurrentLine = Write[i-1]        append.write('Lior ' + CurrentLine.strip('\n') + ' Rozenblit\n')    append.close()def main():    change_list(Count, Write, number_of_lines)    change_file()    readFile = open(FileNameWrite, 'r')    print_new_file = readFile.read()    print(print_new_file)if __name__ == '__main__':    main()